import { addDoc, collection, doc, getDoc, getDocs, setDoc } from "firebase/firestore";
import { useState,useEffect } from "react";
import { TextInput, View,StyleSheet, Button,Text, FlatList } from "react-native"
import { db } from "../firebaseconfig";



const  Datahandle=()=>{
const [name,setName]= useState("");
const [age,setAge]= useState("");
const [reg,setReg]= useState("");
const [docs, setDocs] = useState({
    name:"",
    age:"",
    reg:""

}
);



const writedata= async (data)=>{

try {
     await addDoc(collection(db,'User'),{
        name:name,
        age:age,
        reg:reg
    })
   alert('Data Saved Successfully')
} catch (error) {
    console.log(error);
    
}
}

const readdata= async ()=>{

    try {
        
        const ref= collection(db,'User');
        const datares= await getDocs(ref);
        datares.forEach((doc)=>{
            setDocs(doc.data())
            console.log(docs)
        })
            // setDocs(doc.data)
        
        // if(datares.exists){
        //     setData(datares.data())
        //     console.log(data.name)
        // }
        // else return console.log("data fetch failed")
    } catch (error) {
        console.log(error)
    }
}

const renderitem =(items)=>{
    return(
        <View style={styles.container}>
            <Text>Name:{items?.name}</Text>
            <Text>age:{items?.age}</Text>
            <Text>reg:{items?.reg}</Text>
        </View>
    )
}

    return(
      <View>
        <TextInput 
        placeholder="Name"
        style={styles.input}
        value={name}
        onChangeText={setName}/>
        <TextInput 
        placeholder="age"
        style={styles.input}
        value={age}
        onChangeText={setAge}/>
        <TextInput 
        placeholder="reg"
        style={styles.input}
        value={reg}
        onChangeText={setReg}/>
      <Button 
      title="write data"
      onPress={writedata}/>
      <Button 
      style={{}}
      title="read data"
      onPress={readdata}/>
                <Text>Name:{docs.name}</Text>
            <Text>age:{docs.age}</Text>
            <Text>reg:{docs.reg}</Text>
      
      </View>

    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: "ffff",
      padding: 10,
      paddingTop:50,
    alignItems: 'center',
    },
    header: {
      height: 50,
     
      alignItems: "center",
      justifyContent: "center",
    },
    title: {
      color: "#000",
      fontSize: 20,
      fontWeight: "bold",
    },
    form: {
      marginTop: 20,
    },
    input: {
      height: 40,
      width: 200,
      borderWidth: 1,
      borderColor: "#000",
      borderRadius: 5,
      padding: 10,
      marginTop: 10,
      
    },
    button: {
      backgroundColor: "#000",
      height: 40,
      width: "100%",
      borderRadius: 5,
      marginTop: 20,
    },
    buttonText: {
      color: "#fff",
      fontSize: 18,
      fontWeight: "bold",
      textAlign: "center",
      padding:4
    },
  });
export default Datahandle;
